   --background: #eff7f9;
   --black:#0a0b10;
   --purple:#000000;
   --pink:#e5a1f8;
   --white:#ffffff;
   --nav:#0072bb;
   --nav2:#303030;
}


*,*::before,*::after{
    margin:0;
    padding:0;
    box-sizing:border-box;
    font-family: 'Poppins', sans-serif;
}
html{
  ${"" /* overflow-y: scroll; */}
